import React, { useState } from 'react';
import styled from 'styled-components';
import { Link as ScrollLink } from 'react-scroll'; // Import react-scroll

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggle = () => {
    setIsOpen(!isOpen);
  };

  return (
    <Nav>
      <Logo href="/">
        <img src="https://mir-s3-cdn-cf.behance.net/project_modules/fs/ce33f2102795941.5f3e946eb2e00.jpg" alt="Company Logo" />
      </Logo>
      <Hamburger onClick={toggle}>
        <span />
        <span />
        <span />
      </Hamburger>
      <Menu isOpen={isOpen}>
        <MenuLink to="home" smooth={true} duration={500}>Home</MenuLink>
        <MenuLink to="about" smooth={true} duration={500}>About</MenuLink>
        <MenuLink to="services" smooth={true} duration={500}>Services</MenuLink>
        <MenuLink to="contact" smooth={true} duration={500}>Contact</MenuLink>
      </Menu>
    </Nav>
  );
};

export default Navbar;

const Nav = styled.nav`
  padding: 0 2rem;
  display: flex;
  justify-content: space-between;
  align-items: center;
  flex-wrap: wrap;
  background: #333;
`;

const Logo = styled.a`
  padding: 1rem 0;
  img {
    height: 40px;
  }
`;

const Hamburger = styled.div`
  display: none;
  flex-direction: column;
  cursor: pointer;
  span {
    height: 2px;
    width: 25px;
    background: #fff;
    margin-bottom: 4px;
    border-radius: 5px;
  }

  @media (max-width: 768px) {
    display: flex;
  }
`;

const Menu = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  position: relative;
  width: 100%;
  @media (max-width: 768px) {
    overflow: hidden;
    flex-direction: column;
    max-height: ${({ isOpen }) => (isOpen ? "300px" : "0")};
    transition: max-height 0.3s ease-in;
    width: 100%;
  }
`;

const MenuLink = styled(ScrollLink)`
  padding: 1rem 2rem;
  cursor: pointer;
  text-align: center;
  text-decoration: none;
  color: #fff;
  transition: all 0.3s ease-in;
  font-size: 1.2rem;

  &:hover {
    color: #e91e63;
  }
`;
